use builtin::*;
#[allow(unused_imports)]
use builtin_macros::*;
use vstd::prelude::*;

verus! {

pub struct RingBuffer<T> {
    ring: Ghost<Vec<T>>,
    head: nat,
    tail: nat,
}

impl<T: Copy> RingBuffer<T> {
    pub fn new(capacity: nat) -> (rb: Self)
        ensures
            rb.head == 0,
            rb.tail == 0,
            rb.ring@.len() == capacity,
    {
        RingBuffer {
            ring: ghost(vec![proof_from_false(); capacity]),
            head: 0,
            tail: 0,
        }
    }

    #[verifier::type_invariant]
    closed spec fn inv(&self) -> bool {
        let capacity = self.ring@.len();
        let head = self.head;
        let tail = self.tail;
        let occupant = if tail >= head {
            tail - head
        } else {
            capacity - head + tail
        };
        &&& if capacity == 0 {
            head == 0 && tail == 0
        } else {
            head < capacity && tail < capacity
        }
        &&& occupant <= capacity
    }

    // Other methods not shown for brevity
}

pub trait View {
    type V;
    closed spec fn view(&self) -> Self::V;
}

impl<T: Copy> View for RingBuffer<T> {
    type V = (Seq<T>, nat);

    closed spec fn view(&self) -> Self::V {
        let capacity = self.ring@.len();
        let occupied =
            if self.tail >= self.head {
                self.tail - self.head
            } else {
                capacity - self.head + self.tail
            };
        let seq_data = Seq::new(occupied, |i: int|
            self.ring@[((self.head as int + i) % (capacity as int)) as nat]
        );
        (seq_data, capacity)
    }
}

}

