            &&& self.head < self.ring.len()
            &&& self.tail < self.ring.len()
            &&& self.ring.len() > 0
        }

        pub fn len(&self) -> (ret: usize)
            requires
                self.inv(),
            ensures
                ret == if self.tail >= self.head {
                        self.tail - self.head
                    } else {
                        self.ring.len() - self.head + self.tail
                    },
        {
            proof {
                use_type_invariant(&self);
                lemma_mod_auto(self.ring.len() as int);
            }
            if self.tail > self.head {
                self.tail - self.head
            } else if self.tail < self.head {
                (self.ring.len() - self.head) + self.tail
            } else {
                0
            }
        }

        pub fn has_elements(&self) -> (ret: bool)
            requires
                self.inv(),
            ensures
                ret == (self.head != self.tail),
        {
            proof {
                use_type_invariant(&self);
            }
            self.head != self.tail
        }

        pub fn is_full(&self) -> (ret: bool)
            requires
                self.inv(),
            ensures
                ret == (self.head == ((self.tail + 1) % self.ring.len())),
        {
            proof {
                use_type_invariant(&self);
                lemma_mod_auto(self.ring.len() as int);
            }
            self.head == ((self.tail + 1) % self.ring.len())
        }

        pub fn new(ring: Vec<T>) -> (ret: RingBuffer<T>)
            requires
                ring.len() > 0,
            ensures
                ret.inv(),
                ret.head == 0,
                ret.tail == 0,
                ret.ring@ == ring@,
        {
            RingBuffer {
                head: 0,
                tail: 0,
                ring,
            }
        }

        pub fn enqueue(&mut self, val: T) -> (succ: bool)
            requires
                self.inv(),
            ensures
                succ == !old(self).is_full(),
                self.inv(),
                if succ {
                    self.tail == (old(self.tail) + 1) % self.ring.len(),
                    self.ring@ == old(self.ring)@.update(old(self.tail) as int, val),
                } else {
                    self == old(self),
                },
        {
            if self.is_full() {
                false
            } else {
                proof {
                    use_type_invariant(&*self);
                    lemma_mod_auto(self.ring.len() as int);
                }
                my_set(&mut self.ring, self.tail, val);
                self.tail = (self.tail + 1) % self.ring.len();
                true
            }
        }

        pub fn dequeue(&mut self) -> (ret: Option<T>)
            requires
                self.inv(),
            ensures
                ret.is_Some() == old(self).has_elements(),
                self.inv(),
                if ret.is_Some() {
                    self.head == (old(self.head) + 1) % self.ring.len(),
                    self.tail == old(self.tail),
                    ret == Some(old(self.ring[old(self.head)])),
                } else {
                    self == old(self),
                },
        {
            proof {
                use_type_invariant(&*self);
                lemma_mod_auto(self.ring.len() as int);
            }

            if self.has_elements() {
                let val = self.ring[self.head];
                self.head = (self.head + 1) % self.ring.len();
                Some(val)
            } else {
                None
            }
        }

        pub fn available_len(&self) -> (ret: usize)
            requires
                self.inv(),
            ensures
                ret == self.ring.len().saturating_sub(1 + self.len()),
        {
            proof {
                use_type_invariant(&self);
            }
            self.ring.len().saturating_sub(1 + self.len())
