use vstd::pervasive::*;
use builtin_macros::*;

verus! {

#[verifier::ext_equal]
#[verifier::accept_recursive_types(A)]
pub enum MyOption<A> {
    None,
    Some(A),
}

//--------------------------------------------------------------------------------
// Specification Functions
//--------------------------------------------------------------------------------

pub open spec fn is_Some<A>(opt: MyOption<A>) -> bool {
    match opt {
        MyOption::Some(_) => true,
        MyOption::None => false,
    }
}

pub open spec fn is_None<A>(opt: MyOption<A>) -> bool {
    match opt {
        MyOption::None => true,
        MyOption::Some(_) => false,
    }
}

pub open spec fn get_Some_0<A>(opt: MyOption<A>) -> A {
    match opt {
        MyOption::Some(a) => a,
        MyOption::None => arbitrary(),
    }
}

//--------------------------------------------------------------------------------
// Refined View Function
//--------------------------------------------------------------------------------

pub open spec fn View<A>(opt: MyOption<A>) -> (bool, A) {
    ( is_Some(opt), if is_Some(opt) { get_Some_0(opt) } else { arbitrary() } )
}

impl<A: Clone> Clone for MyOption<A> {
    fn clone(&self) -> Self {
        match self {
            MyOption::None => MyOption::None,
            MyOption::Some(a) => MyOption::Some(a.clone()),
        }
    }
}

impl<A: Copy> Copy for MyOption<A> {}

impl<A> MyOption<A> {
    //--------------------------------------------------------------------------------
    // Spec version of Or
    //--------------------------------------------------------------------------------
    pub open spec fn Or(self, optb: MyOption<A>) -> MyOption<A> {
        match self {
            MyOption::None => optb,
            MyOption::Some(_) => self,
        }
    }

    //--------------------------------------------------------------------------------
    // Executable code
    //--------------------------------------------------------------------------------
    pub fn or(self, optb: MyOption<A>) -> (res: MyOption<A>)
        requires
            true,
        ensures
            res == self.Or(optb),
    {
        match self {
            MyOption::None => optb,
            MyOption::Some(_) => self,
        }
    }

    #[inline(always)]
    pub const fn is_some(&self) -> (res: bool)
        requires
            true,
        ensures
            res == is_Some(*self),
    {
        match self {
            MyOption::Some(_) => true,
            MyOption::None => false,
        }
    }

    #[inline(always)]
    pub const fn is_none(&self) -> (res: bool)
        requires
            true,
        ensures
            res == is_None(*self),
    {
        match self {
            MyOption::Some(_) => false,
            MyOption::None => true,
        }
    }

    pub fn as_ref(&self) -> (a: MyOption<&A>)
        requires
            true,
        ensures
            match *self {
                MyOption::None => a == MyOption::None,
                MyOption::Some(ref x) => a == MyOption::Some(x),
            },
    {
        match self {
            MyOption::Some(x) => MyOption::Some(x),
            MyOption::None => MyOption::None,
        }
    }

    pub fn unwrap(self) -> (a: A)
        requires
            is_Some(self),
        ensures
            a == get_Some_0(self),
    {
        match self {
            MyOption::Some(a) => a,
            MyOption::None => unreached(),
        }
    }

    pub proof fn tracked_unwrap(tracked self) -> (tracked a: A)
        requires
            is_Some(self),
        ensures
            a == get_Some_0(self),
    {
        match self {
            MyOption::Some(a) => a,
            MyOption::None => proof_from_false(),
        }
    }
}

fn test_option_generic() {
    let opt: MyOption<i32> = MyOption::None;
    let is_none = opt.is_none();
    let is_some = opt.is_some();
    assert(is_none);
    assert(!is_some);

    let opt2: MyOption<i32> = MyOption::Some(42);
    let is_none = opt2.is_none();
    let is_some = opt2.is_some();
    assert(!is_none);
    assert(is_some);

    let opt3 = opt.or(opt2);
    let is_some = opt3.is_some();
    let val = opt3.unwrap();
    assert(is_some);
    assert(val == 42);

    let opt4 = opt2.or(opt);
    let is_some = opt4.is_some();
    let val = opt4.unwrap();
    assert(is_some);
    assert(val == 42);

    let opt5 = opt.or(MyOption::None);
    let is_none = opt5.is_none();
    let is_some = opt5.is_some();
    assert(is_none);
    assert(!is_some);
}

} // verus!

